import 'package:flutter/material.dart';
import 'package:qrcode/serach.dart';

class Home extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return Scaffold(
      body:Container(
        decoration: const BoxDecoration(
          image: DecorationImage(image: NetworkImage('data:image/jpeg;base64,'),fit: BoxFit.cover)
        ),
        child: Center(
          child: Column(
            mainAxisAlignment: MainAxisAlignment.center,
            children: [
              const Text('Welcome to ',style: TextStyle(color: Colors.blue),),
              const SizedBox(height: 15),
              Container(
                padding:const EdgeInsets.all(12.0),
                decoration: BoxDecoration(
                  borderRadius: BorderRadius.circular(12.0),
                  color: Colors.white.withOpacity(0.55)
                ),
                child: const Text(
                  'Tischat weather app',
                  style: TextStyle(
                    fontSize: 40,
                    
                  ),
                ),
              )

            ],
          ),
        ),
      ),
      floatingActionButton: FloatingActionButton.extended(
        backgroundColor: Colors.white.withOpacity(0.65),
        onPressed: (){
          Navigator.push(context, MaterialPageRoute(builder: (_)=>const Serach()));
        },
        label: const Text('Search',style: TextStyle(color: Colors.black),),
      ),
    );
  }
}

